import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [reviewReport, setReviewReport] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
    setReviewReport(null); // Clear previous report
    setError(null); // Clear previous errors
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      setError("Please select a file first.");
      return;
    }

    setLoading(true);
    setError(null);
    setReviewReport(null);

    const formData = new FormData();
    formData.append("file", selectedFile);

    try {
      const response = await axios.post("http://localhost:8000/review/", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });
      setReviewReport(response.data);
    } catch (err) {
      setError("Error uploading file or getting review: " + err.message);
      console.error("Upload error:", err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
        <div className="container">
          <a className="navbar-brand" href="/">Code Review Assistant</a>
        </div>
      </nav>

      <div className="container mt-5">
        <div className="card">
          <div className="card-body">
            <h5 className="card-title">Upload Code for Review</h5>
            <div className="form-group">
              <input type="file" className="form-control-file" onChange={handleFileChange} />
            </div>
            <button className="btn btn-primary" onClick={handleUpload} disabled={!selectedFile || loading}>
              {loading ? (
                <>
                  <span className="spinner-border spinner-border-sm" role="status" aria-hidden="true"></span>
                  <span className="sr-only">Loading...</span>
                </>
              ) : (
                "Upload and Review"
              )}
            </button>
            {error && <p className="text-danger mt-2">{error}</p>}
          </div>
        </div>

        {reviewReport && (
          <div className="card mt-4">
            <div className="card-body">
              <h5 className="card-title">Review Report for {reviewReport.filename}</h5>
              {reviewReport.suggestions.length > 0 ? (
                <ul className="list-group">
                  {reviewReport.suggestions.map((suggestion, index) => (
                    <li key={index} className="list-group-item">
                      <strong>Line {suggestion.line}:</strong> {suggestion.suggestion}
                    </li>
                  ))}
                </ul>
              ) : (
                <p>No specific suggestions found. Good job!</p>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
