import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [reviewReport, setReviewReport] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
    setReviewReport(null); // Clear previous report
    setError(null); // Clear previous errors
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      setError("Please select a file first.");
      return;
    }

    setLoading(true);
    setError(null);
    setReviewReport(null);

    const formData = new FormData();
    formData.append("file", selectedFile);

    try {
      const response = await axios.post("http://localhost:8000/review/", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });
      setReviewReport(response.data);
    } catch (err) {
      setError("Error uploading file or getting review: " + err.message);
      console.error("Upload error:", err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Code Review Assistant</h1>
      </header>
      <main>
        <div className="upload-section">
          <input type="file" onChange={handleFileChange} />
          <button onClick={handleUpload} disabled={!selectedFile || loading}>
            {loading ? "Reviewing..." : "Upload and Review"}
          </button>
          {error && <p className="error-message">{error}</p>}
        </div>

        {reviewReport && (
          <div className="review-report">
            <h2>Review Report for {reviewReport.filename}</h2>
            {reviewReport.suggestions.length > 0 ? (
              <ul>
                {reviewReport.suggestions.map((suggestion, index) => (
                  <li key={index}>
                    Line {suggestion.line}: {suggestion.suggestion}
                  </li>
                ))}
              </ul>
            ) : (
              <p>No specific suggestions found. Good job!</p>
            )}
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
